/**
 * Extend object (right to left)
 *
 * @example
 *
 *   var obj = {};
 *   extend(obj, { a: 1, b: 3}, { a: 5, c: 7});
 *   console.log(obj) // { a:5, b:3, c:7 }
 */

function extend() {
  var args  = Array.prototype.slice.call(arguments);
  var obj = args.shift();
  var parent = args.shift();
  if (parent){
    for (var key in parent) {
      obj[key] = parent[key];
    }
    args.unshift(obj);
    return extend.apply(this, args);
  }
  return obj;
}

exports = module.exports = extend;